import axios from 'axios';
import './App.css';

class App extends React.Component{
  state = { advice: ''};

  componentDidMount(){
    this.fetchAdivce();

  }

  fetchAdivce = () => {
    axios.get('https://api.adviceslip.com/advice')
      .then((response)=>{
        console.log(response.data.slip.advice);

      })
      .catch((error)=>{
        console.log(error);
      });
  }

